import React, { Component } from "react";
class book extends Component {
    state = {
        details: {start_time: '', end_time :'' , resource_id :'' }
    }
  
   handleChange = (event) => {
    const cred = this.state.details;
    cred[event.target.name] = event.target.value;
    this.setState({details: cred});
  };

  render(){
  return (
    <form>
      <label>
        
        <select name="version" onChange={this.handleChange} value={this.state.details.start_time}>
          <option value="16.8">6:00-7:00AM</option>
          <option value="16.7">7:00-8:00AM</option>
          <option value="16.6">8:00-9:00AM</option>
          <option value="16.5">6:00-7:00AM</option>
        </select>
      </label>
    </form>
  );
}}

export default book;
